                  <Chip
                    avatar={<Avatar>MB</Avatar>}
                    label="Primary Clickable Chip"
                    clickable
                    className={classes1.chip}
                    color="primary"
                    onDelete={handleDelete1}
                    deleteIcon={<DoneIcon />}
                  />
                  <Chip
                    icon={<FaceIcon />}
                    label="Primary Clickable Chip"
                    clickable
                    className={classes1.chip}
                    color="primary"
                    onDelete={handleDelete1}
                    deleteIcon={<DoneIcon />}
